        t.Errorf("got: %q, want: %q", got, want)
    }
}

func TestAdd(t *testing.T) {
    want := 2
    got := add(1, 1)
    t.Logf("got: %d", got)
    if got != want {
        t.Errorf("got: %d, want: %d", got, want)
    }
}
